function burgerMenu(){const e=document.querySelector(".burger"),c=document.querySelector(".nav-menu"),s=document.querySelector("body");e.addEventListener("click",(()=>{c.classList.contains("active")?(c.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")):(c.classList.add("active"),e.classList.add("active-burger"),s.classList.add("locked"))}));document.querySelectorAll(".nav-menu *").forEach((t=>{t.addEventListener("click",(function(){c.classList.remove("active"),e.classList.remove("active-burger"),s.classList.remove("locked")}))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}burgerMenu(),window.addEventListener("scroll",fixedNav);