function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".nav-menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")?(t.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")):(t.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))}));document.querySelectorAll(".nav-menu *").forEach((a=>{a.addEventListener("click",(function(){t.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")}))}))}function fixedNav(){const e=document.querySelector("nav");window.scrollY>=1?e.classList.add("fixed__nav"):e.classList.remove("fixed__nav")}function hallTabs(){if(!document.querySelector(".hall-layout"))return null;const e=document.querySelectorAll(".hall-layout__btn"),t=document.querySelectorAll(".hall-layout__content");e.forEach((c=>{c.addEventListener("click",(()=>{const a=c.getAttribute("data-tab-btn");e.forEach((e=>e.classList.remove("active"))),t.forEach((e=>e.classList.remove("active"))),c.classList.add("active"),document.querySelector(`.hall-layout__content[data-tab-content="${a}"]`).classList.add("active")}))}))}burgerMenu(),window.addEventListener("scroll",fixedNav),hallTabs(),document.querySelectorAll(".sums__item").forEach(((e,t)=>{e.style.setProperty("--index",t+1)}));